INSERT INTO `cf_updates` (`update_id`, `account_id`, `version`, `title`, `details`, `files`, `is_current`, `released_at`, `created_at`) VALUES
(1, NULL, '2.1', '2.0', '<ul><li>Added : Admin job apply for a candidate.</li><li>Added : \"SSl/TLS\" option in admin email settings.</li><li>Added : Date format setting in admin</li><li>Fixed : Candidate account activation page missing variable issue.</li><li>Fixed : Admin -> Custom Css update isse</li><li>Fixed : Jobs list function parameter issue.</li><li>Fixed : Jobs list pagination issue.</li><li>Fixed : PDF resume -> missing imag issue.</li><li>Fixed : Google login redirect issue.</li><li>Fixed : Job apply, traits insert issue.</li><li>Fixed : Candidate password update issue.</li><li>Fixed : Google login redirect issue when account exist with email.</li></ul>', '', 1, '2023-06-11 00:00:00', '2024-03-12 06:06:27'),
(2, NULL, '2.0', '2.0', '<ul><li>Added : Color panel for color selection.</li><li>Added : Dark mode theme.</li><li>Added : Admin settings for css colors.</li><li>Added : Setting to select number of departments on home page.</li><li>Added : Limit setting for departments on homepage and job list page.</li><li>Added : Settings for department display on home page</li><li>Added : Settings for department filter display on jobs list page.</li><li>Fixed : Email config for ssl added.</li><li>Fixed : HTML formatting issue in job description and long words css apply.</li><li>Fixed : Language loader issue during installation</li><li>Fixed : Missing language translation phrases.</li><li>Fixed : Adjusted php8 compatibility issues.</li></ul>', '', 0, '2022-09-12 00:00:00', '2024-03-12 06:06:27'),
(3, NULL, '1.9', '1.9', '<ul><li>Added : Candidate skills in resume builder.</li><li>Added : Filter added for skills in Admin Candidates listing.</li><li>Added : Option to display jobs to only logged in users.</li><li>Added : Ability to edit overall result in admin job board.</li><li>Fixed : Installer issues</li><li>Fixed : Missing language translation phrases in email templates.</li><li>Fixed : PHP 8 compatibility issues.</li><li>Fixed : Default language issue.</li><li>Fixed : Third party oAuth image issue.</li><li>Fixed : Salary range filter issue in candidate area.</li><li>Fixed : Month names in dates not translatable.</li><li>Fixed : Labels in Settings Apis.</li><li>Fixed : Job link in refer job email.</li></ul>', '', 0, '2022-09-12 00:00:00', '2024-03-12 06:06:27'),
(4, NULL, '1.8', '1.8', '<ul><li>Added : Slugs to jobs via job creation replacing ids.</li><li>Added : On page meta keywords and meta description in jobs create/edit.</li><li>Added : Min and Max Salary fields in jobs and their filters in candidate section.</li><li>Added : Setting for team members to be only able to see their corrected jobs.</li><li>Fixed : Default language not being loaded for candidate section.</li><li>Fixed : Permission names not translatable.</li><li>Fixed : Month names in dates not translatable.</li></ul>', '', 0, '2022-07-27 00:00:00', '2024-03-12 06:06:27'),
(5, NULL, '1.7', '1.7', '<ul><li>Added : Front account button converted to drop down links after login.</li><li>Added : Filters for gender, age, locations in candidates list.</li><li>Added : Filters for gender, age, locations in job board / job applications.</li><li>Added : Candidate interview list in candidate section.</li><li>Added : Setting in admin to display assigned interview to candidate or not.</li><li>Fixed : Missing smtp settings in admin.</li></ul>', '', 0, '2022-05-21 00:00:00', '2024-03-12 06:06:27'),
(6, NULL, '1.6', '1.6', '<ul><li>Added : Support for RTL</li><li>Added : Language Selectors dropdown with flags in admin and candidate area.</li><li>Added : Settings to display language selectors on candidate and admin area.</li><li>Added : Settings for forcing candidates to make resume before applying.</li><li>Added : Validation messages made multi language enabled and translatable.</li><li>Fixed : DB Port issue after installation.</li><li>Fixed : Missing placeholders with language translations for form fields.</li></ul>', '', 0, '2022-03-30 00:00:00', '2024-03-12 06:06:27'),
(7, NULL, '1.5', '1.5', '<ul><li>Added : Display job filters on home banner.</li><li>Added : Delete job application feature from job board.</li><li>Added : Show rejected in candidate area in job applications when rejected.</li><li>Added : Interview view/conduct from job board.</li><li>Fixed : Footer text formatting issue.</li><li>Fixed : Datatables get requests converted to post requests.</li><li>Fixed : Individual job detail page job trait as lang.</li><li>Fixed : Interview pdf download error.</li></ul>', '', 0, '2021-12-05 00:00:00', '2024-03-12 06:06:27'),
(8, NULL, '1.4', '1.4', '<ul><li>Added : Custom filters feature.</li><li>Added : Candidate creation by admin feature.</li><li>Added : Admin login as candidate feature</li><li>Added : Link for document resume in resume view.</li><li>Added : From email address field for mail send with auto detect domain in settings.</li><li>Added : Option to ask for data import during installation.</li><li>Fixed : Banner and logo upload issue.</li><li>Fixed : Linkedin oAuth remote request issue.</li><li>Fixed : Word wrap issue in description of job detail and blog detail page.</li><li>Fixed : Admin Job list bulk action issue.</li><li>Fixed : Email templates static logo issue.</li><li>Removed : Companies module as purpose can be served with job filters.</li></ul>', '', 0, '2021-05-17 00:00:00', '2024-03-12 06:06:27'),
(9, NULL, '1.3', '1.3', '<ul><li>Added : Enable/Disable multiple resume feature.</li><li>Added : Search candidates by job titles and experience in candidates pool.</li><li>Fixed : \"strict_sql_mode\" issue.</li><li>Fixed : Candidate Interview remarks issue.</li></ul>', '', 0, '2021-11-19 00:00:00', '2024-03-12 06:06:27'),
(10, NULL, '1.2', '1.2', '<ul><li>Added : Date, Time and Location field in interview assignment.</li><li>Added : Notifications to user and team member when interview assigns.</li><li>Added : Notification to user when quiz assigns.</li><li>Added : Message candidate feature.</li><li>Fixed : Setting -> Api redirect uri issue during installation.</li><li>Removed : Alphabetic charachter restriction from some fields to support multi language.</li></ul>', '', 0, '2020-07-06 00:00:00', '2024-03-12 06:06:27'),
(11, NULL, '1.1', '1.1', '<ul><li>Added : Automatic update feature.</li><li>Fixed : Intaller issues.</li><li>Fixed : Some missing icons.</li></ul>', '', 0, '2020-06-03 00:00:00', '2024-03-12 06:06:27');
